        className="text-4xl md:text-6xl font-bold mt-6"
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
      >
        Hi, I'm <span className="text-cyan-400">Your Name</span>
      </motion.h1>
