export default function Skills() {
  return (
    <section id="skills" className="py-20 bg-gray-900">
      <h2 className="text-3xl font-bold text-center mb-10 text-cyan-400">Skills</h2>
      <div className="max-w-4xl mx-auto flex flex-wrap justify-center gap-6">
        {Object.entries(skills).map(([category, subSkills], i) => (
          <div
            key={i}
            className="relative group px-6 py-3 bg-gray-800 rounded-full shadow-md cursor-pointer hover:bg-cyan-400 hover:text-black transition"
          >
            <span className="font-semibold">{category}</span>

            {/* Hidden list - appears on hover */}
            <div className="absolute left-1/2 transform -translate-x-1/2 mt-3 w-max bg-gray-800 text-white rounded-xl shadow-lg p-4 opacity-0 group-hover:opacity-100 group-hover:translate-y-0 transition-all duration-300 z-10">
              <ul className="space-y-1 text-sm">
                {subSkills.map((skill, idx) => (
                  <li key={idx} className="hover:text-cyan-400 transition">
                    {skill}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
